var GetUniversityName = Class.create();
GetUniversityName.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getUniversityName: function() {
		var uniRecord = new GlideRecord('x_1052872_needit_univerity_load_data_university');
		uniRecord.get(this.getParameter('sysparm_userID'));
		//gs.addInfoMessage(this.getParameter('sysparm_userID'));
		//gs.addInfoMessage(uniRecord.u_university_name);
		return uniRecord.u_university_name + '';
	},
    type: 'GetUniversityName'
});
