sys_script_include.script:
var CourseChoiceScriptInclude = Class.create();
CourseChoiceScriptInclude.prototype = {
  initialize: function() {},

  getCourseChoices: function(university_name) {
    var choices = [];

    if (university_name) {
      var courseGr = new GlideRecord('x_1052872_needit_university_and_course_enrollment');
      courseGr.addQuery('u_reference_1',university_name);
      courseGr.query();

      while (courseGr.next()) {
        var courseId = courseGr.getValue('sys_id');
        var courseName = courseGr.getValue('u_reference_2');
        choices.push({
          value: courseId,
          label: courseName
        });
      }
    }

    return choices;
  },

  type: 'CourseChoiceScriptInclude'
};
