sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

    // Add your code here
    // Get student preferences
    var preference1 = source.university1.toString();
    var preference2 = source.university2.toString();
    var preference3 = source.university3.toString();

    // Query university table for available seats
    var universities = new GlideRecord('x_1052872_needit_university_enrollment_table');
    universities.query();
	while (universities.next()) {
    var universityName = universities.u_string_2.toString();
    var availableSeats = universities.total_available_seats.toString();

	if (preference1 === universityName && availableSeats > 0) {
      // Allocate preference1 university to the student
      target.university_alloted = preference1;
      universities.total_available_seats = universities.total_available_seats-1;
      universities.update();
      break;
    } else if (preference2 === universityName && availableSeats > 0) {
      // Allocate preference2 university to the student
      target.university_alloted = preference2;
      universities.total_available_seats = universities.total_available_seats-1;
      universities.update();
      break;
    } else if (preference3 === universityName && availableSeats > 0) {
      // Allocate preference3 university to the student
      target.university_alloted = preference3;
      universities.total_available_seats = universities.total_available_seats-1;
      universities.update();
      break;
    }
  }

  return;


})(source, map, log, target);
